public class LogoutEndpoint {
    private final TokenStore tokenStore;
    private final SessionManager sessionManager;

    public LogoutEndpoint(TokenStore tokenStore, SessionManager sessionManager) {
        this.tokenStore = tokenStore;
        this.sessionManager = sessionManager;
    }

    @PostMapping("/logout")
    public ResponseEntity<LogoutResponse> logout(HttpServletResponse response) {
        String sessionCookie = response.getHeader("Cookie");
        if (sessionCookie != null) {
            sessionManager.invalidateSession(sessionCookie);
        }

        String accessToken = tokenStore.getAccessTokenForSession(sessionCookie);
        if (accessToken != null) {
            tokenStore.revokeAccessToken(accessToken);
        }

        response.addHeader("Set-Cookie", "session=; Max-Age=0");
        return ResponseEntity.ok(new LogoutResponse("Logged out successfully"));
    }
}

public interface TokenStore {
    String getAccessTokenForSession(String sessionCookie);
    void revokeAccessToken(String accessToken);
}

public interface SessionManager {
    void invalidateSession(String sessionCookie);
}
