const qs = require('querystring');

app.use(express.urlencoded({ extended: false }))

app.get('/search', (req, res) => {
    const query = qs.escape(req.query.q);
    app.spotifyHelper.search(query, 50, '', (error, data) => {
        if (error) {
            console.error(error);
            res.sendStatus(500);
            return;
        }
        const artists = data.tracks.items.map(artist => artist.name);
        res.json(artists);
    });
});
app.get('/user-top', (req, res) => {
    const userId = req.query.user_id;
    app.spotifyHelper.getUserTopTracks(userId, 50, (error, data) => {
        if (error) {
            console.error(error);
            res.sendStatus(500);
            return;
        }
        const tracks = data.items.map(track => track.name);
        res.json(tracks);
    });
});
app.get('/song-recommendations', (req, res) => {
    const songId = req.query.song_id;
    app.spotifyHelper.getSongRecommendations(songId, 10, (error, data) => {
        if (error) {
            console.error(error);
            res.sendStatus(500);
            return;
        }
        const songs = data.tracks.map(song => song.name);
        res.json(songs);
    });
});
app.get('/get-random-song', (req, res) => {
    app.spotifyHelper.getRandomSong((error, data) => {
        if (error) {
            console.error(error);
            res.sendStatus(500);
            return;
        }
        const song = data.name;
        res.json({ song });
    });
});
